import React, { useState, useEffect } from 'react'
import './P.css'
import Layout from '../../components/shared/Layout/Layout'
import { getPortfolio } from '../../services/products'
import { useParams, Link } from 'react-router-dom'

const Portfolio = (props) => {

    const [portfolio, setPortfolio] = useState(null)
    const [isLoaded, setLoaded] = useState(false)
    const { id } = useParams()

    useEffect(() => {
        const fetchProduct = async () => {
            const portfolio = await getPortfolio(id)
            setPortfolio(portfolio)
            setLoaded(true)
        }
        getPortfolio()
    }, [id])

    if (!isLoaded) {
        return <h1>Loading...</h1>
    }

    return (
        <Layout>
            <div className="portfolio-detail">
                <img className="portfolio-detail-image" src={portfolio.imgURL} alt={portfolio.name} />
                <div className="detail">
                    <div className="name">{portfolio.name}</div>
                    <div className="description">{portfolio.description}</div>
                </div>
            </div>
        </Layout>
    )
}

export default Portfolio
